mapper.update:
update t_itemplan
itemcode =#{itemCode}, planmonth = #{planMonth}, department=#{department}, plannumber=#{planNumber}, purpose=#{purpose}
where id = #{id}

mapper.select:
select id,itemCode,name as itemName,model as itemModel,unit as itemUnit,planNumber,department,planMonth
from t_itemplan a,t_item b

	 a.itemcode = b.code 
	 and itemcode like '%${itemCode}%'	
	 and planmonth = #{planMonth}
	and department=#{department}
